let _log = require("./logger2");
let folderLogs = "C:\\Temp\\ci-logmodule\\";
let data = {
    "pathFolder": folderLogs,
    "maxLogSizeMB": 10,
    "backups": 5,
    "fileName": "test.log",
    "level": "INFO"
};

_log.init(data);


let url = 'localhost';
let port = 88;

_log.write('Hola mundo...');
_log.write('http://{0}:{1}'.format(url, port));
_log.write('Error', _log.level.ERROR);
_log.write('Debug', _log.level.DEBUG);
_log.write('Warning', _log.level.WARNING);
_log.write('Fatal', _log.level.FATAL);
_log.write('Info', _log.level.INFO);

let _indexInterval1=1;
let _indexInterval2=10000;
let dataAux = new Array(1024 * 10).join('*');

setInterval(function () {
    _log.write("_indexInterval1:{0}:{1}".format(_indexInterval1, dataAux));
    _indexInterval1++;
}, 100);

setInterval(function () {
    _log.write("_indexInterval2:{0}:{1}".format(_indexInterval2, dataAux));
    _indexInterval2++;
}, 100);

