function PrintNumbers(start, end, cb) {
    try
    {
        for(let i=start; i<end; i++)
        {
            _log.write("Number:{0}".format(i));
        }

    }
    catch (err) {
        console.log("Error exception:".format(err));
    }
    cb(null);
}